import React, { useState } from 'react';
import './App.css'; // Add your CSS styles here

const blocks = [
  { id: 1, name: 'Warm Up', type: 'bar', fill: 100 },
  { id: 2, name: 'Active', type: 'bar', fill: 50 },
  { id: 3, name: 'Cool Down', type: 'bar', fill: 40 },
  { id: 4, name: 'Two Step Ramp', type: 'steps', fill: [25, 50] },
  { id: 5, name: 'Ramp Up', type: 'ramp', fill: [25, 50, 75, 100] },
  { id: 6, name: 'Ramp Down', type: 'ramp', fill: [100, 75, 50, 25] },
];

const App = () => {
  const [graphBlocks, setGraphBlocks] = useState([]);

  const handleDragStart = (block) => (event) => {
    event.dataTransfer.setData('block', JSON.stringify(block));
  };

  const handleDrop = (event) => {
    event.preventDefault();
    const block = JSON.parse(event.dataTransfer.getData('block'));
    setGraphBlocks([...graphBlocks, block]);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <div className="app-container">
      {/* Left Section */}
      <div className="left-panel">
        <h3>Click or drag the blocks to build workout</h3>
        <div className="blocks">
          {blocks.map((block) => (
            <div
              key={block.id}
              className="block"
              draggable
              onDragStart={handleDragStart(block)}
              title={block.name} // Display name on hover
            >
              {block.type === 'bar' ? (
                <div className="bar" style={{ height: `${block.fill}%`, backgroundColor: '#6c5ce7' }}></div>
              ) : (
                <div className="steps">
                  {block.fill.map((step, index) => (
                    <div
                      key={index}
                      className="step"
                      style={{ height: `${step}%`, backgroundColor: '#6c5ce7' }}
                    ></div>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Right Section */}
      <div className="right-panel" onDrop={handleDrop} onDragOver={handleDragOver}>
        <h3>Click the blocks or drag them here to begin building your workout.</h3>
        <div className="graph">
          {graphBlocks.map((block, index) => (
            <div
              key={index}
              className="graph-block"
              style={{ width: `${100 / graphBlocks.length}%`, backgroundColor: '#a29bfe' }}
            >
              {block.name}
            </div>
          ))}
        </div>
      </div>

      {/* Bottom Section */}
      <div className="bottom-panel">
        <h3>Details of Added Blocks</h3>
        <div className="details">
          {graphBlocks.map((block, index) => (
            <div key={index} className="detail-block">
              {block.name}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
