import React, { useState } from "react";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import "./App.css";

const App = () => {
  const [workoutBlocks, setWorkoutBlocks] = useState([]);

  const blocks = [
    { id: "warm-up", type: "warm-up", name: "Warm Up" },
    { id: "active", type: "active", name: "Active" },
    { id: "cool-down", type: "cool-down", name: "Cool Down" },
    { id: "two-step-ramp", type: "two-step-ramp", name: "Two Step Ramp" },
    { id: "ramp-up", type: "ramp-up", name: "Ramp Up" },
    { id: "ramp-down", type: "ramp-down", name: "Ramp Down" },
  ];

  const clearBlocks = () => {
    setWorkoutBlocks([]);
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const { source, destination } = result;

    // If dragging within the workout area
    if (source.droppableId === "workout-track" && destination.droppableId === "workout-track") {
      const reorderedBlocks = Array.from(workoutBlocks);
      const [movedBlock] = reorderedBlocks.splice(source.index, 1);
      reorderedBlocks.splice(destination.index, 0, movedBlock);
      setWorkoutBlocks(reorderedBlocks);
    }

    // If dragging from the sidebar to the workout area
    if (source.droppableId === "sidebar" && destination.droppableId === "workout-track") {
      const newBlock = {
        id: `block-${Date.now()}`, // Unique ID for each block
        ...blocks[source.index], // Copy block details
      };
      const updatedBlocks = Array.from(workoutBlocks);
      updatedBlocks.splice(destination.index, 0, newBlock);
      setWorkoutBlocks(updatedBlocks);
    }
  };

  const Block = ({ type, name }) => {
    return (
      <div className="block">
        <div className={`block-visual ${type}`}>
          {type === "two-step-ramp" && (
            <>
              <div className="bar quarter"></div>
              <div className="bar half"></div>
            </>
          )}
          {type === "ramp-up" && (
            <>
              <div className="bar decreasing"></div>
              <div className="bar medium"></div>
              <div className="bar increasing"></div>
              <div className="bar small"></div>
            </>
          )}
          {type === "ramp-down" && (
            <>
              <div className="bar increasing"></div>
              <div className="bar medium"></div>
              <div className="bar decreasing"></div>
              <div className="bar small"></div>
            </>
          )}
        </div>
        <p>{name}</p>
      </div>
    );
  };

  const WorkoutArea = () => {
    return (
      <DragDropContext onDragEnd={handleDragEnd}>
        <div className="workout-area">
          <button className="clear-btn" onClick={clearBlocks}>
            Clear Blocks
          </button>
          <Droppable droppableId="workout-track">
            {(provided) => (
              <div
                className="workout-track"
                {...provided.droppableProps}
                ref={provided.innerRef}
              >
                {workoutBlocks.length === 0 ? (
                  <p>Drag blocks here to build your workout.</p>
                ) : (
                  workoutBlocks.map((block, index) => (
                    <Draggable key={block.id} draggableId={block.id} index={index}>
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className="block"
                        >
                          <Block type={block.type} name={block.name} />
                        </div>
                      )}
                    </Draggable>
                  ))
                )}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </div>
      </DragDropContext>
    );
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="app-container">
        {/* Sidebar Section */}
        <Droppable droppableId="sidebar" isDropDisabled={true}>
          {(provided) => (
            <div
              className="sidebar"
              {...provided.droppableProps}
              ref={provided.innerRef}
            >
              <h3>Click or drag the blocks to build workout</h3>
              <div className="block-grid">
                {blocks.map((block, index) => (
                  <Draggable key={block.id} draggableId={block.id} index={index}>
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                      >
                        <Block type={block.type} name={block.name} />
                      </div>
                    )}
                  </Draggable>
                ))}
              </div>
              {provided.placeholder}
            </div>
          )}
        </Droppable>

        {/* Workout Area */}
        <WorkoutArea />

        {/* Bottom Section */}
        <div className="bottom-section">
          <h3>Details of Added Blocks</h3>
          {workoutBlocks.length === 0 ? (
            <p>No blocks added yet.</p>
          ) : (
            <div className="details-list">
              {workoutBlocks.map((block, index) => {
                let distance = index % 3 + 1; // Distribute distance dynamically
                return (
                  <div key={index} className="detail-item">
                    <span className="detail-name">
                      {block.name} ({distance} km)
                    </span>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>
    </DndProvider>
  );
};

export default App
